    /**
     * @covers Html::&__get
     * @covers Html::__set
     * @covers Html::__isset
     * @covers Html::__unset
     */
    public function testMagicMethods() {
      $e = Html::a()->href('')->style(array('b' => 'r', 'k' => 'v'))->style(array('w' => 'k'));
      $this->assertEquals(array('b' => 'r', 'k' => 'v', 'w' => 'k'), $e->style);  //get
      $this->assertTrue(empty($e->class));  //isset
      $this->assertFalse(isset($e->class)); //isset
      $e->id = 'pokus'; //set
      $this->assertEquals('pokus', $e->id); //get
      $this->assertEquals('<a href="" style="b: r; k: v; w: k" id="pokus"></a>', $e);
      $e->id = null;  //set
      $this->assertEquals('', $e->id);
      unset($e->id);  //unset
      $this->assertTrue(empty($e->id));
      $this->assertEquals('<a href="" style="b: r; k: v; w: k"></a>', $e);
    }

