    /**
     * @covers Html::insert
     */
    public function testInsert()
    {
        //add
        $e = Html::strong()->setText('ahoj')->add(Html::span()->setText('teext'))->setText('cuzz');
        $this->assertEquals('<strong>ahoj<span>teext</span>cuzz</strong>', $e);

        $e = Html::strong()->add(Html::span());
        $this->assertEquals('<strong><span></span></strong>', $e);

        //test na null polozky
        $e = Html::strong()->add(null);
        $this->assertEquals('<strong></strong>', $e);

        $e = Html::strong()->add(array(Html::span(), null));
        $this->assertEquals('<strong><span></span></strong>', $e);

        $e = Html::strong()->add(array(null, Html::span()));
        $this->assertEquals('<strong><span></span></strong>', $e);

        $e = Html::strong()->add(array(null, Html::span(), null));
        $this->assertEquals('<strong><span></span></strong>', $e);

        $e = Html::strong()->add(array(null, null))->add(null);
        $this->assertEquals('<strong></strong>', $e);

        $e = Html::strong()->add(array(Html::span(), null, Html::span()));
        $this->assertEquals('<strong><span></span><span></span></strong>', $e);

        $e = Html::strong()->add(array(Html::span(), Html::em()));
        $this->assertEquals('<strong><span></span><em></em></strong>', $e);

        //test pretezovani parametru
        $e = Html::strong()->add(Html::span(), null);
        $this->assertEquals('<strong><span></span></strong>', $e);

        $e = Html::strong()->add(null, Html::span());
        $this->assertEquals('<strong><span></span></strong>', $e);

        $e = Html::strong()->add(null, Html::span(), null);
        $this->assertEquals('<strong><span></span></strong>', $e);

        $e = Html::strong()->add(null, null)->add(null);
        $this->assertEquals('<strong></strong>', $e);

        $e = Html::strong()->add(Html::span(), null, Html::span());
        $this->assertEquals('<strong><span></span><span></span></strong>', $e);

        $e = Html::strong()->add(Html::span(), Html::em());
        $this->assertEquals('<strong><span></span><em></em></strong>', $e);

        //insert
        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, Html::video());
        $this->assertEquals('<strong><p></p><video></video><em></em><b></b><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, Html::video(), true);
        $this->assertEquals('<strong><p></p><video></video><b></b><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, Html::video(), true, null);
        $this->assertEquals('<strong><p></p><video></video><b></b><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, Html::video(), true, 2);
        $this->assertEquals('<strong><p></p><video></video><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, array(Html::bu(), Html::ui()));
        $this->assertEquals('<strong><p></p><bu></bu><ui></ui><em></em><b></b><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, array(Html::bu(), Html::ui()), true);
        $this->assertEquals('<strong><p></p><bu></bu><ui></ui><b></b><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, array(Html::bu(), Html::ui()), true, null);
        $this->assertEquals('<strong><p></p><bu></bu><ui></ui><i></i><u></u></strong>', $e);

        $e = Html::strong()->add(array(Html::p(), Html::em(), Html::b(), Html::i(), Html::u()));
        $e->insert(1, array(Html::bu(), Html::ui()), true, 3);
        $this->assertEquals('<strong><p></p><bu></bu><ui></ui><u></u></strong>', $e);
    }

