    /**
     * @covers Html::__toString
     */
    public function test__toString()
    {
        $e = Html::element('strong')->setText('ahoj svete');
        $this->assertEquals('<strong>ahoj svete</strong>', $e->__toString());

        $e = Html::element('strong')->setText('ahoj svete');
        $this->assertEquals('<strong>ahoj svete</strong>', $e);


        $menuskel = function($row) { return Html::a()->href('adresa', array('a' => 'b', 'c' => $row))->setText($row); };

        $this->assertTrue(is_callable($menuskel));

        $this->assertEquals('<a href="adresa?a=b&amp;c=5">5</a>', $menuskel(5));

        $r = null;
        foreach (range(1, 5) as $row) {
          $r[] = $menuskel($row);
        }
        $e = Html::span()->add($r);

        $this->assertEquals('<span><a href="adresa?a=b&amp;c=1">1</a><a href="adresa?a=b&amp;c=2">2</a><a href="adresa?a=b&amp;c=3">3</a><a href="adresa?a=b&amp;c=4">4</a><a href="adresa?a=b&amp;c=5">5</a></span>', $e);

        //TODO dalsi testy na skeleton navrh!
        //tip: s 2-rozmernym polem
    }

    /**
     * @covers Html::save
     */
    public function testSave()
    {
        $e = Html::element('strong')->setText('ahoj svete')->save();
        $this->assertSame('<strong>ahoj svete</strong>', (string) Html::load($e));

        $this->assertSame('<strong>ahoj svete</strong>', Html::load($e)->render());
    }

    /**
     * @covers Html::load
     */
    public function testLoad()
    {
        $this->assertSame('<a href="adresa?a=b&amp;c=22">33</a>', (string) Html::load($this->test->t3));

        $this->assertSame('<a href="adresa?a=b&amp;c=22">33</a>', Html::load($this->test->t3)->render());
    }

    //testy na ArrayAccess pristup
    public function testArrayAccess() {
      $e = Html::span()->title('spaaan')->add(
                                                    Html::div()->id('diiiiv'),
                                                    Html::a()->class('aaa')
                                                  );
      $this->assertCount(2, $e->getChildren());
      $this->assertEquals('<a class="aaa"></a>', $e[1]);
      $this->assertTrue(isset($e[0]));
      $this->assertEquals('<div id="diiiiv"></div>', $e[0]);
      unset($e[0]);
      $this->assertCount(1, $e->getChildren());
      $e[1] = Html::div()->title('tit');
      $this->assertCount(2, $e->getChildren());
      $this->assertEquals('<div title="tit"></div>', $e[1]);
      $e[] = Html::p();
      $this->assertCount(3, $e->getChildren());
      $this->assertEquals('<span title="spaaan"><a class="aaa"></a><div title="tit"></div><p></p></span>', $e);
    }
